CREATE TABLE users(id SERIAL PRIMARY KEY, name VARCHAR(255) NOT NULL, email VARCHAR(255) NOT NULL UNIQUE, phoneNumber VARCHAR(11) NOT NULL UNIQUE, username VARCHAR(255) NOT NULL UNIQUE, password VARCHAR(255) NOT NULL);
CREATE TABLE places(id SERIAL PRIMARY KEY, userID int REFERENCES users(id), listingTitle VARCHAR(255) NOT NULL, description TEXT NOT NULL, address VARCHAR(255) NOT NULL UNIQUE, ppn float NOT NULL); 
CREATE TABLE avails(id SERIAL PRIMARY KEY, placesID int REFERENCES places(id), start DATE NOT NULL, end DATE NOT NULL);
CREATE TABLE bookings(id SERIAL PRIMARY KEY, placesID int REFERENCES places(id), userID int REFERENCES users(id), bookingdates DATERANGE NOT NULL);